import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";
import { AnimatePresence } from "framer-motion";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import About from "./pages/About";
import Projects from "./pages/Projects";
import Contact from "./pages/Contact";
import Footer from "./components/Footer";
import { useState } from "react";
import WelcomeOverlay from "./components/WelcomeOverlay";

function App() {
  const [overlayVisible, setOverlayVisible] = useState(true);

  return (
    <Router>
      {overlayVisible && (
        <WelcomeOverlay onFinish={() => setOverlayVisible(false)} />
      )}
      <div
        className={`bg-white dark:bg-black text-black dark:text-white overflow-x-hidden min-h-screen flex flex-col transition-opacity duration-500 ${
          overlayVisible ? "opacity-0" : "opacity-100"
        }`}
      >
        <Navbar />
        <main className="flex-grow w-full overflow-x-hidden">
          <AnimatedRoutes />
        </main>
        <Footer />
      </div>
    </Router>
  );
}

// ✅ Define this as a separate component *inside* the Router
function AnimatedRoutes() {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/projects" element={<Projects />} />
        <Route path="/contact" element={<Contact />} />
      </Routes>
    </AnimatePresence>
  );
}

export default App;
