import PageWrapper from "../components/PageWrapper";
import { motion } from "framer-motion";

export default function Home() {
  return (
    <PageWrapper>
      <section className="min-h-screen flex flex-col md:flex-row items-center justify-center bg-blue-100 px-6 md:px-20 py-16 gap-12">
        {/* Left Side - Text */}
        <motion.div
          className="md:w-1/2 text-center md:text-left"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-4xl md:text-5xl font-bold text-blue-800 mb-4">
            Hi, I'm Mohammad ðŸ‘‹
          </h1>
          <p className="text-lg text-gray-700 mb-6">
            I build creative, responsive websites using React, Tailwind, and
            animation tools like Framer Motion.
          </p>
          <a
            href="/contact"
            className="inline-block px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
          >
            Letâ€™s Work Together
          </a>
        </motion.div>

        {/* Right Side - Image/Illustration */}
        <motion.div
          className="md:w-1/2 flex justify-center"
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <img
            src="https://cdn.dribbble.com/users/1162077/screenshots/3848914/programmer.gif"
            alt="Developer Illustration"
            className="rounded-xl shadow-md max-w-full"
          />
        </motion.div>
      </section>
    </PageWrapper>
  );
}
