import { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function WelcomeOverlay() {
  const [visible, setVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setVisible(false), 4000); // 4 seconds
    return () => clearTimeout(timer);
  }, []);

  return (
    <AnimatePresence>
      {visible && (
        <motion.div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 text-white"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center p-8 rounded"
          >
            <div
              className="absolute inset-0 bg-cover bg-center opacity-20"
              style={{ backgroundImage: "url('/your-photo.jpg')" }} // ðŸ‘ˆ put your image here
            ></div>
            <h1 className="text-4xl md:text-5xl font-bold z-10 relative">
              Welcome to my portfolio
            </h1>
            <p className="text-lg md:text-xl mt-4 z-10 relative">
              I'm Mohammad Rajabi â€“ Web Developer & Problem Solver
            </p>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}
