import PageWrapper from "../components/PageWrapper";
import { motion, useInView } from "framer-motion";
import { useRef } from "react";

const projects = [
  {
    title: "Telegram Bot + Google Sheets",
    description:
      "A Telegram bot with dynamic menus, integrated with Google Sheets.",
    link: "https://github.com/yourusername/telegram-bot",
  },
  {
    title: "Personal Portfolio Website",
    description:
      "This portfolio site built with React + Tailwind + Framer Motion.",
    link: "https://github.com/yourusername/portfolio",
  },
  {
    title: "Weather App",
    description:
      "A weather app using OpenWeatherMap API, React, and responsive design.",
    link: "https://github.com/yourusername/weather-app",
  },
];

export default function Projects() {
  // Create refs for each project card
  const cardRefs = projects.map(() => useRef(null));
  const inViews = cardRefs.map((ref) => useInView(ref, { once: true }));

  return (
    <PageWrapper>
      <section className="min-h-screen bg-gray-100 dark:bg-gray-900 py-16 px-6 md:px-20 transition-colors duration-500">
        <h2 className="text-3xl font-bold text-center text-blue-600 dark:text-blue-400 mb-12">
          My Projects
        </h2>

        <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
          {projects.map((project, index) => (
            <motion.div
              ref={cardRefs[index]}
              key={index}
              initial={{ opacity: 0, y: 30 }}
              animate={inViews[index] ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md border border-gray-200 dark:border-gray-700 hover:shadow-xl transition-all duration-300"
            >
              <h3 className="text-xl font-semibold mb-2 text-gray-800 dark:text-white">
                {project.title}
              </h3>
              <p className="text-gray-600 dark:text-gray-300 mb-4">
                {project.description}
              </p>
              <a
                href={project.link}
                target="_blank"
                rel="noreferrer"
                className="text-blue-500 dark:text-blue-400 hover:underline"
              >
                View on GitHub â†’
              </a>
            </motion.div>
          ))}
        </div>
      </section>
    </PageWrapper>
  );
}
