import { useEffect } from "react";
import { motion } from "framer-motion";
import yourPhoto from "../assets/your-photo.jpg"; // or wherever your image is

export default function WelcomeOverlay({ onFinish }) {
  useEffect(() => {
    const timer = setTimeout(() => {
      onFinish(); // hides overlay after 3 seconds
    }, 3000);
    return () => clearTimeout(timer);
  }, [onFinish]);

  return (
    <motion.div
      className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-[9999]"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div
        className="absolute inset-0 bg-cover bg-center blur-sm opacity-30"
        style={{ backgroundImage: `url(${yourPhoto})` }}
      />
      <div className="relative text-white text-center px-6 z-10">
        <h1 className="text-4xl sm:text-5xl font-bold mb-4">
          Welcome to my portfolio
        </h1>
        <p className="text-xl sm:text-2xl">
          I'm Mohammad Rajabi â€“ Web Developer & Problem Solver
        </p>
      </div>
    </motion.div>
  );
}
