import { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function WelcomeOverlay() {
  const [visible, setVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setVisible(false), 3500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <AnimatePresence>
      {visible && (
        <motion.div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/80"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <div className="absolute inset-0 overflow-hidden">
            {/* Example: floating code symbols */}
            <motion.span
              className="absolute text-green-500 text-4xl"
              animate={{ y: [0, -200] }}
              transition={{ loop: Infinity, duration: 5 }}
            >
              {"</>"}
            </motion.span>
            {/* Add more animated shapes or snippets here */}
          </div>
          <motion.div
            className="relative text-center text-white space-y-4 p-4"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-4xl md:text-5xl font-bold">
              Welcome to my portfolio
            </h1>
            <p className="text-lg md:text-xl">
              I'm Mohammad Rajabi â€“ Web Developer & Problem Solver
            </p>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}
